/* eslint func-names: ["error", "never"] */

// current year
$('#year').text(new Date().getFullYear());

// config slider
$('.carousel').carousel({
	interval: 6000,
	pause: 'hover'
});

// slick
$('.slider').slick({
	infinite: true,
	slideToShow: 1,
	slideToScroll: 1
});

// Lightbox
function lightboxCall(event) {
	event.preventDefault();
	$(this).ekkoLightbox();
}
$(document).on('click', '[data-toggle="lightbox"]', lightboxCall);

// add active
$('.navbar-nav li a').each(function () {
	const location = window.location.href;
	const link = this.href;
	if (location === link) {
		$('.navbar-nav li').removeClass("active");
		$(this).addClass('active');
	}
});

// video play
$(function () {
	$(".video").click(function () {
		const theModal = $(this).data("target");
		const videoSRC = $(this).attr("data-video");
		const videoSettings = "?modestbranding=1&rel=0&controls=0&showinfo=0&html5=1&autoplay=1"
		const videoSRCauto = videoSRC + videoSettings;
		const iframe = ' iframe';
		const buttonClose = ' button.close';
		$(theModal + iframe).attr('src', videoSRCauto);
		$(theModal + buttonClose).click(function () {
			$(theModal + iframe).attr('src', videoSRC);
		});
	});
});