$('#year').text(new Date().getFullYear());

// init scroll spy
$('body').scrollspy({
	target: '#main-nav'
});

// smooth scroll
// check hash
function checkHash(e) {
	if (this.hash !== '') {
		e.preventDefault();

		const [hash] = [this.hash];

		$('html, body').animate({
			scrollTop: $(hash).offset().top
		}, 800, function initHash() {
			window.location.hash = hash
		});
	}
}
$('#main-nav a').on('click', checkHash);